css:
@media print {
        .print-format {
            width: 105mm;
        }

        .main {
            /*margin-left: 4mm;*/
            /*margin-right: 4mm;*/
            display: grid;
            grid-template-columns: repeat(3, 33.33%);

            height: auto;
        }
        .qr-item {
            /*margin-top: 1mm;*/
            margin-left: 3mm;
            margin-right: 3mm;
            width: 35mm;
            height: 25mm;
            /*text-align: center;*/
            flex-direction: column;
            justify-content: space-between;
            writing-mode: tb-rl;
            transform: rotate(-180deg);
            clear: both;
        }
        .print-format {
            align-items: center;
            justify-content: center;
        }
    }

    .padding-p{
        padding-top:6px;
    }
    .bold{
        font-weight: 900
    }
    .mid-bold{
        font-weight: 800
    }
    .li-bold{
        font-weight: 600
    }
    .big-size{
        font-size: 16px;
    }
    .mid-size{
        font-size: 10px;
    }
    .mid-low-size{
        font-size: 14px;
    }
    .li-size{
        font-size: 7px;
    }
    p{
        line-height:100%;
    }
