css:
@media print {
        .print-format {
            width: 80mm;
        }

        .main {
            /*margin-left: 4mm;*/
            /*margin-right: 4mm;*/
            display: grid;
            grid-template-columns: repeat(2, 50%);

            height: auto;
        }
        .qr-item {
            /*margin-top: 1mm;*/
            margin-left: 6mm;
            margin-right: 6mm;
            width: 40mm;
            height: 30mm;
            /*text-align: center;*/
            flex-direction: column;
            justify-content: space-between;
        }
        .print-format {
            align-items: center;
            justify-content: center;
        }
    }

    .bold{
        font-weight: 900
    }
    .mid-bold{
        font-weight: 800
    }
    .li-bold{
        font-weight: 600
    }
    .big-size{
        font-size: 16px;
    }
    .mid-size{
        font-size: 10px;
    }
    .li-size{
        font-size: 6px;
    }
    .topleft{
        position: relative;
        float:left;
        width:60%;
        height:18mm;
        padding: 1px 0px 2px 2px;
    }
    .absolute {
      /*position: absolute;*/
      /*bottom: 0px;*/
    } 
    .topright{
        position: relative;
        float:right;
        width:40%;
        height:18mm;
        padding: 5px 5px 5px 5px;
    }
    .bottomleft{
        float:left;
        width:80%;
        height:6mm;
        text-align:left;
    }
    .bottomright{
        float:right;
        width:80%;
        height:6mm;
        margin-right:3mm;
        text-align:right;
    }
