css:
@media print {
        .print-format {
            width: 100mm;
        }

        .main {
            /*margin-left: 4mm;*/
            /*margin-right: 4mm;*/
            display: grid;
            grid-template-columns: repeat(2, 50%);

            height: auto;
        }
        .qr-item {
            /*margin-top: 1mm;*/
            margin-left: 6mm;
            margin-right: 6mm;
            width: 50mm;
            height: 50mm;
            /*text-align: center;*/
            flex-direction: column;
            justify-content: space-between;
        }
        .print-format {
            align-items: center;
            justify-content: center;
        }
    }

    .bold{
        font-weight: 900
    }
    .mid-bold{
        font-weight: 800
    }
    .li-bold{
        font-weight: 600
    }
    .big-size{
        font-size: 19px;
    }
    .mid-size{
        font-size: 12px;
    }
    .li-size{
        font-size: 8px;
    }
    .topleft{
        position: relative;
        float:left;
        width:60%;
        height:27mm;
        padding: 5px 0px 5px 5px;
    }
    .absolute {
      position: absolute;
      bottom: 0px;
    } 
    .topright{
        position: relative;
        float:right;
        width:40%;
        height:27mm;
        padding: 5px 5px 5px 0px;
    }
    .bottomleft{
        float:left;
        width:80%;
        /*height:20mm;*/
        text-align:left;
    }
    .bottomright{
        float:right;
        width:80%;
        /*height:20mm;*/
        text-align:right;
        padding: 5px 8px 0px 0px;
    }
    p{
        line-height:100%;
    }
