css:
@media print {
        .print-format {
            width: 80mm;
        }

        .main {
            /*margin-left: 4mm;*/
            /*margin-right: 4mm;*/
            display: grid;
            grid-template-columns: repeat(1, 100%);

            height: auto;
        }
        .qr-item {
            /*margin-top: 1mm;*/
            margin-left: 3mm;
            margin-right: 3mm;
            width: 80mm;
            height: 50mm;
            /*text-align: center;*/
            flex-direction: column;
            justify-content: space-between;
            clear: both;
        }
        .print-format {
            align-items: center;
            justify-content: center;
        }
    }

    .center{
        text-align:center;
    }
    .right{
        text-align:right;
    }
    .padding-p{
        padding-top:6px;
    }
    .bold{
        font-weight: 900
    }
    .mid-bold{
        font-weight: 800
    }
    .li-bold{
        font-weight: 600
    }
    .big-size{
        font-size: 16px;
    }
    .mid-size{
        font-size: 12px;
    }
    .mid-low-size{
        font-size: 14px;
    }
    .li-size{
        font-size: 10px;
    }
