css:
@media print {
        .print-format {
            width: 105mm;
        }

        .main {
            /*margin-left: 4mm;*/
            /*margin-right: 4mm;*/
            display: grid;
            grid-template-columns: repeat(3, 33.33%);

            height: auto;
        }
        .qr-item {
            /*margin-top: 1mm;*/
            margin-left: 3mm;
            margin-right: 3mm;
            height:25mm;
            width: 35mm;
            /*text-align: center;*/
            flex-direction: column;
            justify-content: space-between;

            clear: both;
        }
        .print-format {
            align-items: center;
            justify-content: center;
        }
    }

    /*.padding-p{*/
